mapper.update:
update thread
set hit = hit+1
where id = #{id,jdbcType=INTEGER}

update thread
set replyCount = replyCount-1
where id = #{id,jdbcType=INTEGER}

update thread
set topped = #{topped}
where id = #{id,jdbcType=INTEGER}

update thread
set deleted = 1
where id = #{id,jdbcType=INTEGER}

update thread set replyCount=replyCount+1, 
author_last_replied_id=#{replyId}, dateLastReplied=#{date}
where id=#{id}
